        <script type="text/javascript" src="scripts/pagos.js"></script>
        <script type="text/javascript" src="../public/js/dist/jspdf.min.js"></script>
        <script type="text/javascript">
            function recibo(){
                var cliente= document.getElementById("idcliente").value;
                
                var cuota= document.getElementById("cuota").value;
                if((cliente =="") || (usuario=="") || (cuota=="")){
                    alert("debes llenar todos los campos para imprimir un recibo")
                }else{
                    imprimir();
                }
            }
            function imprimir(){
                var now = new Date();
                var day = ("0" + now.getDate()).slice(-2);
                var month = ("0" + (now.getMonth() + 1)).slice(-2);
                var today = now.getFullYear()+"-"+(month)+"-"+(day) ;
                var cliente= document.getElementById("idcliente").options[document.getElementById('idcliente').selectedIndex].text;
                var usuario= document.getElementById("usuario").value;
                var cuota= document.getElementById("cuota").value;
                var imgData = 'data:image/png;base64,';

                var doc = new jsPDF();
                doc.setFontSize(22);
                doc.setTextColor(255,0,0);
                doc.text(20, 20, 'RECIBO');

                doc.setFontSize(8);
                doc.setTextColor(0,0,255);
                doc.text(20, 25, 'Pago');

                doc.setTextColor(0,0,0);
                doc.setFontSize(16);
                doc.text(140, 20, 'Fecha');

                doc.setFontSize(17);
                doc.text(140, 30, today);

                

                doc.setLineWidth(1);
                doc.line(20,35, 180, 35);

                doc.setFontSize(19);
                doc.text(20, 60, 'SE RECIBE DE :'+cliente);

                doc.setFontSize(19);
                doc.text(20, 70, 'EL MONTO DE  :'+cuota+'bs');

                doc.setLineWidth(1);
                doc.line(20,75, 180, 75);
                
                doc.setFontSize(14);
                doc.text(20, 90, 'Este recibo es un comprobante del pago de cuotas que realizan nuestros clientes');

                doc.addImage(imgData, 'PNG', 40, 110, 140, 110)

                doc.setFontSize(8);
                doc.text(20, 280, 'IMPORTANTE: se aclara que este recibo no tiene valor legal');
                

                


                doc.save('Recibo'+cliente+'.pdf');
            }
        </script>
            
        
        
